    // 计算两个字符串的差异性 基于Levenshtein距离（矩阵计算）和最大可能的字符串长度返回0-100的相似度，
    function calculateLevenshteinSimilarity(str1, str2) {
        var len1 = str1.length;
        var len2 = str2.length;
        var matrix = [];

        for (var i = 0; i <= len1; i++) {
            matrix[i] = [i];
        }

        for (var j = 0; j <= len2; j++) {
            matrix[0][j] = j;
        }

        for (i = 1; i <= len1; i++) {
            for (j = 1; j <= len2; j++) {
                var cost = str1[i - 1] === str2[j - 1] ? 0 : 1;
                matrix[i][j] = Math.min(
                    matrix[i - 1][j] + 1,
                    matrix[i][j - 1] + 1,
                    matrix[i - 1][j - 1] + cost
                );
            }
        }

        var maxLength = Math.max(len1, len2);
        var distance = matrix[len1][len2];
        var similarity = (1 - distance / maxLength) * 100;

        return similarity;
    }

    // 根据calculateLevenshteinSimilarity找出相似度最高的问题
    function getMostSimilarQuestionIndex(selectedText) {
        var highestSimilarity = 0;
        var mostSimilarQuestion = '';
        for (var i = 0; i < questionBank.length; i++) {
            var similarity = calculateLevenshteinSimilarity(selectedText, questionBank[i].question);
            if (similarity > highestSimilarity) {
                highestSimilarity = similarity;
                //      mostSimilarQuestion = questionBank[i].question; // 这里我们存储的是问题本身
                mostSimilarQuestion = i; // 这里我们存储的是问题的索引，而不是问题本身
            }
        }
        return mostSimilarQuestion;
    }

